import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useState } from "react";

import MD5  from "crypto-js/md5"
const API_URL=process.env.REACT_APP_BASE_URL

export const Marvel = () => {
  const { id } = useParams();
  const [item, setItem] = useState();

  const getHash=(ts,secretKey,publicKey)=>{
    return MD5(ts+secretKey+publicKey).toString()
}

  const fetch = async () => {
    let REACT_APP_BASE_URL = "https://gateway.marvel.com";
    let REACT_APP_API_KEY = "08978b28aeb1c1293567d36665aa58dd";
    let REACT_APP_PRIVATE_KEY = "79d9e9e1c9383d23c66ff829e819803ffc24a551";

    let baseUrl = `${API_URL}/v1/public/characters`;

    let ts = Date.now().toString();
    let apiKey = process.env.REACT_APP_API_KEY;
    let privateKey = process.env.REACT_APP_PRIVATE_KEY;
    let hash = getHash(ts, privateKey, apiKey);

    let url = `${baseUrl}?ts=${ts}&apikey=${apiKey}&hash=${hash}&nameStartsWith=${value}`;

    const res = await axios.get(url);
    setItem(res.data.data.results[0]);
  };
  fetch();
  return (
    <>
      {!item ? (
        ""
      ) : (
        <div className="box-content">
          <div className="right-box">
            <img
              src={`${item.thumbnail.path}.${item.thumbnail.extension}`}
              alt=""
            />
          </div>
          <div className="left-box">
            <h1>{item.name}</h1>
            <h4>{item.description}</h4>
          </div>
        </div>
      )}
    </>
  );
};
