import React from "react"
import { Card } from "./Card"
import axios from "axios"
import { useState } from "react"
import { useEffect } from "react"

import MD5  from "crypto-js/md5"
const API_URL=process.env.REACT_APP_BASE_URL

const getHash=(ts,secretKey,publicKey)=>{
  return MD5(ts+secretKey+publicKey).toString()
}

export const Main = () => {
  // const [url,setUrl]=useState("http://gateway.marvel.com/v1/public/characters?ts=1&apikey=2e1cdeec426ae323484f29024084c206&hash=d516513ba95b9407c7aca0f73b241f8a")
  const [item,setItem]=useState();
  const [search,setSearch]=useState("");
  useEffect(()=>{
    const fetch=async(value)=>{
      // fetch
      let baseUrl = `${API_URL}/v1/public/characters`;

    let ts = Date.now().toString();
    let apiKey = process.env.REACT_APP_API_KEY;
    let privateKey = process.env.REACT_APP_PRIVATE_KEY;
    let hash = getHash(ts, privateKey, apiKey);

    let url = `${baseUrl}?ts=${ts}&apikey=${apiKey}&hash=${hash}&nameStartsWith=${value}`;


      // end
      // fetch
      try{
        let response=await fetch(url)
        let data=await response.json()
        console.log(data.data.results);
        setItem(data.data.results);
        return data.data.results

    }catch(err){
        console.error(err);
        return 
    }

      // end fetch


      // const res=await axios.get(url)

    }
    fetch();
  },[])

  const searchMarvel=()=>{
    setUrl(url)
  }

  return (
    <>
        <div className="header">
            <div className="bg">
                <img src="./Images/bg.png" alt="" />
            </div>
            <div className="search-bar">
                <img src="./Images/logo.jpg" alt="logo" />
                <input type="search" placeholder='Search Here'
                 className='search'
                 onChange={e=>setSearch(e.target.value)}
                 onKeyPress={searchMarvel}/>
            </div>
        </div>
       <div className="content">

        {
          (!item)?<p>Not Found</p>:<Card data={item}/>
        }
       </div>
    </>
  )
}
